mapper.insert:
insert into api_group (case_list, group_mark, project_id
)
value
(
#{caseList}
,
#{groupMark}
,
#{projectId}

)

mapper.select:
select user_type
from api_group
where id = #{id};

select test_list
from api_group
where id = #{id}

select *
from api_group
where id = #{id}
  and project_id = #{projectId}

update api_group
set case_list = #{caseList},
    group_mark=#{groupMark}
where project_id = #{projectId}
  and id = #{id}

mapper.update:
update api_group
set is_del = 0
where id = #{id};

mapper.select:
    select a.api_case_mark as apiCaseMark,uri.api_path as apiPath from apicase a,uri where uri.id = a.api_id and a.id
    in 
    #{id}
)
